mapper.select:
select

from feedback
where
true

  AND work_order_id = #{workOrderId,jdbcType=BIGINT}


  AND customer LIKE concat('%', #{customer,jdbcType=VARCHAR},'%')


  AND title LIKE concat('%', #{title,jdbcType=VARCHAR},'%')


  AND feedback_text LIKE concat('%', #{feedbackText,jdbcType=VARCHAR},'%')


  AND create_time >= #{createTimeStart,jdbcType=TIMESTAMP}


  AND create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  ORDER BY ${sort}


  ${order}
