mapper.select:
select

from work_order
where
true

  AND title LIKE concat('%', #{title,jdbcType=VARCHAR},'%')


  AND description LIKE concat('%', #{description,jdbcType=VARCHAR},'%')


  AND customer = #{customer,jdbcType=VARCHAR}


  AND order_id = #{orderId,jdbcType=VARCHAR}


  AND receptionist LIKE concat('%', #{receptionist,jdbcType=VARCHAR},'%')


  AND type_id = #{typeId,jdbcType=BIGINT}


  AND urgent_degree = #{urgentDegree,jdbcType=INTEGER}


  AND status = #{status,jdbcType=INTEGER}


  AND finish_time >= #{finishTimeStart,jdbcType=TIMESTAMP}


  AND finish_time <= #{finishTimeEnd,jdbcType=TIMESTAMP}


  AND create_time >= #{createTimeStart,jdbcType=TIMESTAMP}


  AND create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  ORDER BY ${sort}


  ${order}
